import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles((theme) => ({
	button: {
		color: 'white',
		background: 'orange',
		border: 'none',
		borderRadius: '16px',
		fontSize: '20px',
	},
	modal: {
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'center',
	},
	paper: {
		backgroundColor: theme.palette.background.paper,
		border: '2px solid #000',
		boxShadow: theme.shadows[5],
		padding: theme.spacing(2, 4, 3),
	},
}));

export default function TransitionsModal() {
	const classes = useStyles();
	const [open, setOpen] = React.useState(false);

	const handleOpen = () => {
		setOpen(true);
	};

	const handleClose = () => {
		setOpen(false);
	};

	return (
		<div>
			<button type="button" onClick={handleOpen} className={classes.button}>
				Conoce Más ✚
			</button>
			<Modal
				aria-labelledby="transition-modal-title"
				aria-describedby="transition-modal-description"
				className={classes.modal}
				open={open}
				onClose={handleClose}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 500,
				}}
			>
				<Fade in={open}>
					<div className={classes.paper}>
						<h2 id="transition-modal-title">iCash</h2>
						<h2 id="transition-modal-title">¿Que es iCash de Procapital?</h2>
						<p id="transition-modal-description">
							•Somos un equipo de profesionales con amplía experiencia en el sector financiero, tanto como
							privado.
							<br></br>•Tenemos la capacidad económica para satisfacer las necesidades de crédito de
							nuestros clientes.
							<br></br>•Contamos con la autorización de la CONDUSEF y CNBV para operar como SOFOM.
						</p>
						<h2 id="transition-modal-title">Productos Procapital</h2>
						<p id="transition-modal-description">
							Préstamos personales con descuentos vía nómina a trabajadores en activo.
							<br></br>Contamos con dos productos:
							<br></br>•Crédito tradicional
							<br></br>•Anticipo de nómina
						</p>
						<h2 id="transition-modal-title">Crédito tradicional</h2>
						<p id="transition-modal-description">
							A través de un convenio de colaboración administrativa entre 
                            la empresa de PROCAPITAL,<br></br> realizamos préstamos a sus empleados,
                             con las siguientes características:
						</p>
					</div>
				</Fade>
			</Modal>
		</div>
	);
}
